import { useState, useEffect } from "react";
import Card from "./components/card";

function App() {
  const [randomImages, setRandomImages] = useState([]);

  useEffect(() => {
    async function fetchImages() {
      const urls = [];
      for (let i = 0; i < 8; i++) {
        const response = await fetch("https://picsum.photos/800");
        urls.push(response.url);
      }
      setRandomImages(urls);
    }

    fetchImages();
  }, []);

  const handleCardClick = (index) => {
    const newRandomImages = [...randomImages];
    const temp = newRandomImages[index];
    newRandomImages[index] = newRandomImages[0];
    newRandomImages[0] = temp;
    setRandomImages(newRandomImages);
  };

  return (
    <div className="App">
      {randomImages.map((image, index) => (
        <Card
          key={index}
          image={image}
          onClick={() => handleCardClick(index)}
        />
      ))}
    </div>
  );
}

export default App;
