import { useState, useEffect } from "react";
import "./App.css";
import Card from "./components/card";

function App() {
  const [randomImage, setRandomImage] = useState(null);

  async function getRandomImages(numImages) {
    const response = await fetch(
      `https://picsum.photos/v2/list?limit=${numImages}`
    );
    const data = await response.json();
    return data.map((image) => image.download_url);
  }

  useEffect(() => {
    async function fetchRandomImages() {
      const numCards = 3; // set the number of cards you have
      const images = await getRandomImages(numCards);
      setRandomImages(images);
    }
    fetchRandomImages();
  }, []);

  return (
    <div className="App">
      {randomImages.map((image, index) => (
        <Card key={index} image={image} />
      ))}
    </div>
  );
}

export default App;
