import { useState, useEffect } from "react";
import Card from "./components/card";
import Points from "./components/points";

function App() {
  const [randomImages, setRandomImages] = useState([]);
  const [currentPoints, setCurrentPoints] = useState(0);
  const [bestScore, setBestScore] = useState(0);
  const [clickedImages, setClickedImages] = useState([]);

  const handlePointsUpdate = (newPoints) => {
    setCurrentPoints(newPoints);
    if (newPoints > bestScore) {
      setBestScore(newPoints);
    }
  };

  useEffect(() => {
    async function fetchImages() {
      const urls = [];
      for (let i = 0; i < 8; i++) {
        let randomPoke = Math.round(Math.random() * 899);
        const response = await fetch(
          `https://pokeapi.co/api/v2/pokemon/${randomPoke}/`
        );
        const data = await response.json();
        urls.push(data.sprites.front_default);
      }
      setRandomImages(urls);
    }

    fetchImages();
  }, []);

  const handleCardClick = (clickedIndex) => {
    if (clickedImages.includes(clickedIndex)) {
      console.log("you lose");
      setClickedImages([]);
      setCurrentPoints(0);
    } else {
      const shuffledImages = [...randomImages].sort(() => Math.random() - 0.5);
      setRandomImages(shuffledImages);
      setClickedImages([...clickedImages, clickedIndex]);
      handlePointsUpdate(currentPoints + 1);

      if (clickedImages.length === 7) {
        console.log("you win");
        setClickedImages([]);
        setCurrentPoints(0);
      }
    }
  };

  return (
    <div className="App">
      <Points currentPoints={currentPoints} bestScore={bestScore} />
      {randomImages.map((image, index) => (
        <Card key={index} image={image} onClick={handleCardClick(image)} />
      ))}
    </div>
  );
}

export default App;
